<style>
  .projectHead {
    width: 95%;
    margin: 60px 30px 30px 20px;
    color: #fff;
    display: flex;
    flex-direction: row-reverse;
    justify-content: space-between;
    font-family: Outfit;
  }
  .projectFirst {
    width: 75%;
  }
  .parent {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(2, 1fr);
    grid-column-gap: 40px;
    grid-row-gap: 30px;
  }

  .divHeight {
    width: 100%;
    height: 230%;
    border-radius: 10px;
  }

  .div1 {
    grid-area: 1 / 1 / 2 / 2;
  }
  .div2 {
    grid-area: 1 / 2 / 2 / 3;
  }
  .div3 {
    grid-area: 1 / 3 / 2 / 4;
  }
  .div4 {
    grid-area: 2 / 1 / 3 / 2;
  }
  .div5 {
    grid-area: 2 / 2 / 3 / 3;
  }
  .projectTitleSecond {
    width: 25%;
    padding: 0 20px 20px 30px;
  }
  .bolderTitle {
    font-weight: 500;
    font-size: 2vw;
    margin: 0 0 20px 0;
  }
  .inDetails {
    line-height: 25px;
    font-weight: 300;
    font-size: 1.4vw;
  }
  .logoStyling {
    width: 100%;
    height: 100px;
    background-color: #77ceff;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 10px;
  }

  .div2 .logoStyling {
    background-color: #77ffbe;
  }
  .div3 .logoStyling {
    background-color: #d3ff77;
  }
  .div4 .logoStyling {
    background-color: #ff8f77;
  }
  .div5 .logoStyling {
    background-color: #77ffe7;
  }
  .logoStyling svg {
    width: 80px;
    height: 80px;
  }
  .title {
    margin: 20px 0 0 0;
    font-size: 1.3rem;
    font-weight: 500;
  }
  .det {
    margin: 10px 0 0 0;
    font-size: 1.1rem;
    line-height: 26px;
    font-weight: 300;
  }
  .designBtn {
    background: none;
    color: #fff;
    border: none;
    margin: 20px 30px 0 0px;
    /* display: flex;
    align-items: center; */
    height: 30px;
    font-family: Outfit;
    font-weight: 500;
  }
  .designBtn a img {
    margin: 0 10px 0 0;
    transition: 0.2s;
  }
  .designBtn a {
    text-decoration: none;
    color: #fff;
    font-size: 1.5vw;
    transition: 0.2s;
  }
  .btnBlue {
    transform: rotate(-45deg);
  }
  .flex {
    display: flex;
    align-items: center;
  }
  .designBtn a:hover {
    color: var(--bright-blue);
  }
  .designBtn a:hover .btnBlue {
    filter: invert(0%) sepia(69%) saturate(6288%) hue-rotate(118deg)
      brightness(103%) contrast(102%);
    transform: rotate(-45deg) translateX(3px) translateY(0px);
  }
  @media only screen and (max-width: 675px) {
    .projectHead {
      width: 100%;
      display: block;
      margin: 450px 0 0 0;
    }
    .projectTitleSecond {
      width: 100%;
      font-size: 1rem;
      margin: 0 20px 10px 0px;
      padding: 0 10px 10px 20px;
    }
    .bolderTitle {
      font-weight: 500;
      font-size: 1.8rem;
      margin: 0 0 20px 10px;
    }
    .inDetails {
      line-height: 25px;
      font-weight: 300;
      font-size: 1.3rem;
      margin: 0 0 0 10px;
    }
    .projectFirst {
